jQuery(document).ready(function($){	var formlabel = 0;	var old_send_to_editor = window.send_to_editor;	var old_tb_remove = window.tb_remove;
	$('.tk_fileuploader').click(function(){		formlabel = jQuery(this).parent();		tb_show('', 'media-upload.php?type=image&TB_iframe=true');				// Workaround for problems with thickbox and tabs			$('#TB_window,#TB_overlay,#TB_HideSelect').one("unload",killthickbox);						function killthickbox(e) {			e.stopPropagation();			e.stopImmediatePropagation();			return false;		}	});		window.send_to_editor = function(html) {		imgurl = $( 'img', html ).attr( 'src' );		elementurl = $( html ).attr( 'href' );				if( imgurl == undefined ){			url = elementurl;		}else{			url = imgurl;			formlabel.find( 'img' ).attr({ 				src: imgurl			});		}		formlabel.find( 'input[type=text]' ).val( url );		tb_remove();	}});